$(document).ready(function(){var $blogPostContent=$(".blog-post-content");var $blogImages=$blogPostContent.find("img");var $blogULBr=$blogPostContent.find("ul").find("br").remove();$.each($blogImages,function(idx,obj){$(obj).wrap("<div class='centered-img'></div>")});var $rating=$(".rating");if($rating.length>0){$rating.rating()}var $cardTitle=$(".card-title");$cardTitle.each(function(){if($(this).text().length>22&&$(this).parent().parent().attr("class")!=="card blog-intro-card"){$(this).css("font-size","0.9rem").css("margin","0 auto")}});var $headlineH1=$(".headline-content").find("h1");$headlineH1.each(function(){if($(this).text().length>=30){$(this).css("font-size","1.9rem")}});var $entryContent=$(".entry-content");var heights=$entryContent.map(function(){return $(this).height()}).get();var maxHeight=Math.max.apply(null,heights);$entryContent.each(function(){$(this).css("height",maxHeight)});var $cardH3=$("h3[rel='bookmark']");var cardHeights=$cardH3.map(function(){return $(this).height()}).get();var cardHeadlineHight=Math.max.apply(null,cardHeights);$cardH3.each(function(){$(this).css("height",cardHeadlineHight)});var files=$("input[type=file]");files.each(function(){var name=$(this).attr("value");console.log(name);$(this).parent().find("label[for=validatedCustomFile]").text(name)})});